import {
    FETCH_PIZZA_FAILURE,
    FETCH_PIZZA_START,
    FETCH_PIZZA_SUCCESS
}
from 'actionTypes'
import {fetchPizza as fetchPizzaApi} from 'api'


export const fetchPizza = () => async dispatch => {
    dispatch({type : FETCH_PIZZA_START})

    try {
        const pizza = await fetchPizzaApi()
        dispatch({
            type: FETCH_PIZZA_SUCCESS,
            payload: pizza
        })
    }
    catch(err) {
        dispatch({
            type: FETCH_PIZZA_FAILURE,
            payload: err,
            error: true
        })
    }
}
