import  '../../main.css'
import  '../../cart.css'

import React, {Component} from 'react'
import {createStore, applyMiddleware} from 'redux'
import reducers from 'reducers'
import thunk from 'redux-thunk'
import {composeWithDevTools} from 'redux-devtools-extension'
import {Provider} from 'react-redux'
import {Router, Route, browserHistory} from 'react-router'
import Layout from 'containers/layout'
import Pizza from 'containers/pizza/pizza'
import Deserts from 'containers/pizza/deserts'
import Salats from 'containers/pizza/salats'
import HomePasta from 'containers/pizza/home_pasta'
import Basket from 'containers/basket'
import About from 'containers/about'
import Contact from 'containers/contact'
import Events from 'containers/events'
import Admin from 'containers/admin'
import Category from 'containers/category'
const store = createStore(reducers, composeWithDevTools(applyMiddleware(thunk)))


export default class App extends Component{
    render() {
      return <Provider store={store}>
          <Router history={browserHistory}>
              <Route component = {Layout} >
              <Route path='/'  component={Category} />
                  <Route path='/pizza'  component={Pizza} />
                  <Route path='/home_pasta'  component={HomePasta} /> 
                  <Route path='/deserts'  component={Deserts} /> 
                  <Route path='/salats'  component={Salats} />
                  <Route path='/about' component={About} />
                  <Route path='/contact' component={Contact} />
                  <Route path='/events' component={Events} />
                  <Route path='/basket' component={Basket} />
                  <Route path='/admin' component={Admin} />
              </Route>
          </Router>
      </Provider>
    }
}


