import React from 'react'
import {connect} from 'react-redux'
import R from 'ramda'
import InfoAboutDelivery from 'containers/basket/infoAboutDelivery'
import FromDelivery from 'containers/basket/formDelivery'

import {
       getTotalBasketPrice,
       getBasketPizzaWithCount
} from 'selectors'

const Basket = ({pizza, totalPrice}) => {
    const isBasketEmpty = R.isEmpty(pizza)
    const renderContent = () => (
       <div className='row'>
           {isBasketEmpty && <div>Корзина пуста</div>}
           <div className='table-responsive'>
               <table className='table-bordered  table-condensed cf'>
                   <tbody>
                   {pizza.map((p, index) => (
                       <tr
                           key={index}
                           className='item-checout'
                       >
                           <td
                           className='first-column-checkout'>
                               <img
                               className='img-thumbnail'
                               src={p.image}
                               alt={p.name}
                               />
                           </td>
                           <td>{p.name}</td>
                           <td>{p.price}</td>
                           <td>{p.count}</td>
                           <td>
                               <span className='delete-cart'></span>
                           </td>
                       </tr>
                   ))}
                   </tbody>
               </table>
           </div>
           {
               R.not(isBasketEmpty) &&
                   <div className='row'>
                       <div className='pull-right total-user-checkout'>
                           <b>К оплате: {totalPrice} руб.</b>
                       </div>
                   </div>
           }
           <br/><br/>
           {!isBasketEmpty && <InfoAboutDelivery />}
           {R.not(isBasketEmpty) &&  <FromDelivery />}
       </div>
    )
    const renderSideBar = () => (
        <div></div>
    )
    return (
        <div className='view-container'>
            <div className='container'>
                <div className='row'>
                    <div className='col-md-9'>
                        {renderContent()}
                    </div>
                    <div className='col-md-3 btn-user-checkout'>
                        {renderSideBar()}
                    </div>
                </div>
            </div>
        </div>
    )
}

const mapStateToProps = state => ({
    pizza: getBasketPizzaWithCount(state),
    totalPrice: getTotalBasketPrice(state)
})
export default  connect(mapStateToProps, null)(Basket)
