import React, {Component} from 'react'
import {Link} from 'react-router'
import R from 'ramda'
import {connect} from 'react-redux'

import {fetchPizza, addPizzaToBasket} from 'actions'
import {getPizza} from 'selectors'

class Pizza extends Component {
    componentDidMount(){
    this.props.fetchPizza()
    }

    renderPizza(pizza, index) {
        const {addPizzaToBasket} = this.props;
        const shortDeacription = pizza.description.length   >  90 ?`${R.take(90, pizza.description)} ...` : pizza.description
        return (
            <div className='col-sm-4 col-lg-4 col-md-4 book-list' key={index}>
                <div className='thumbnail'>
                    <img
                        className='img-thumbnail'
                        src={pizza.image}
                        alt={pizza.name}
                    />
                    <div
                        className='caption' style = {{height: 180}}>
                            <p className='text-primary'><Link
                                to={`/pizza/${pizza.id}`}>
                                {pizza.name}
                            </Link>
                            </p>
                        <p className='text-muted'>{shortDeacription}</p>
                        <h4 className='pull-right'><p>{pizza.price} руб.</p></h4>
                        <div className='itemButton'>
                            <button
                                className='btn btn-success'
                            onClick={() => addPizzaToBasket(pizza.id)}
                            >
                                В корзину
                            </button>
                     {/*       <Link
                                to={`/pizza/${pizza.id}`}
                                className='btn btn-default'>
                                Подробнее
                            </Link>*/}
                        </div>
                    </div>
                </div>
            </div>
        )
    }
    render() {
        const {pizza} = this.props;
        return(
            <div className='books row'>
                {pizza.map((p, index) => this.renderPizza(p, index))}
            </div>
        )
    }
}

const mapStateToProps = state => ({
    pizza : getPizza(state)
})

const mapDispatchToProps = {
    fetchPizza,
    addPizzaToBasket
}
export default connect(mapStateToProps, mapDispatchToProps)(Pizza)

