import R from 'ramda'

import {
    ADD_PIZZA_TO_BASKET,
    REMOVE_PIZZA_FROM_BASKET,
    REMOVE_PIZZA_FROM_BASKET_BY_INDEX,
    ADD_ORDER_TO_FIREBASE

} from  'actionTypes'

const initialState = []

export default (state = initialState, {type, payload}) => {
    switch (type){
        case ADD_PIZZA_TO_BASKET:
            return R.append(payload, state)
        case REMOVE_PIZZA_FROM_BASKET:
            return R.without(R.of(payload), state)
        case REMOVE_PIZZA_FROM_BASKET_BY_INDEX:
            let newVelue = []
            state.map((id, index) => {
                if(index !== payload)
                    newVelue.push(id)
            })
            return newVelue;
        case ADD_ORDER_TO_FIREBASE:
            return state
        default:
            return state
    }
}


