import R from 'ramda'
import { FETCH_PIZZA_SUCCESS, LOAD_GOOGLE_DATA, FETCH_GOODS_SUCCESS } from 'actionTypes'

const initialState = {
    googleCoord: false,
    goods: false
}

export default (state = initialState, { type, payload }) => {
    switch (type) {
        case FETCH_PIZZA_SUCCESS:
            const newValue = R.indexBy(R.prop('id'), payload)
            return R.merge(state, newValue)
        case LOAD_GOOGLE_DATA:
            return { ...state, googleCoord: payload }
        case FETCH_GOODS_SUCCESS:
            return { ...state, goods: payload }
        default:
            return state
    }
}
