import R from 'ramda'
import { FETCH_PIZZA_SUCCESS, LOAD_GOOGLE_DATA } from 'actionTypes'

const initialState = {
    googleCoord: false
}

export default (state = initialState, { type, payload }) => {
    switch (type) {
        case FETCH_PIZZA_SUCCESS:
            const newValue = R.indexBy(R.prop('id'), payload)
            return R.merge(state, newValue)

        case LOAD_GOOGLE_DATA:
            return {...state, googleCoord: payload }

        default:
            return state
    }
}
