import {FETCH_PIZZA_SUCCESS} from 'actionTypes'
import R from 'ramda'

const initialState = {
    ids: []
}

export default (state = initialState, {type, payload}) => {
    switch (type){
        case FETCH_PIZZA_SUCCESS:
            return R.merge(state,{
                ids : R.pluck('id', payload)
            })
        default:
            return state
    }
}
